mapper.select:
select NOTICE_NUM as "num", ADMIN_ID as "id", NOTICE_TYPE as "type", NOTICE_SUBJECT as "subject", NOTICE_CONTENT as "content", NOTICE_DATE as "date", NOTICE_READCOUNT as "readcount", NOTICE_IMAGE as "image" from NOTICE
where NOTICE_SUBJECT like CONCAT('%', #{search}, '%') or NOTICE_CONTENT like CONCAT('%', #{search}, '%')
order by NOTICE_NUM desc limit #{startRow}, #{pageSize}

mapper.update:
update NOTICE
set NOTICE_SUBJECT = #{subject}, NOTICE_CONTENT = #{content}, NOTICE_TYPE = #{type}
where NOTICE_NUM = #{num}
