mapper.select:
select * from NOTICE
order by NOTICE_NUM desc
limit #{startRow}, #{pageSize}

mapper.update:
update NOTICE
set NOTICE_READCOUNT = NOTICE_READCOUNT + 1
where NOTICE_NUM = #{num}

mapper.select:
select * from FAQ
order by FAQ_NUM desc
limit #{startRow}, #{pageSize}

mapper.update:
update QNA
set QNA_ANSWER= #{content}
where NOTICE_NUM = #{num}

mapper.select:
select QNA_QUESTION as "content", QNA_SUBJECT as "subject", MEM_ID as "name"
from QNA
WHERE QNA_NUM = #{num}
