mapper.select:
select FAQ_NUM as "num", ADMIN_ID as "id", FAQ_SUBJECT as "subject", FAQ_CONTENT as "content" from FAQ
where FAQ_SUBJECT LIKE CONCAT('%', #{search}, '%') or FAQ_CONTENT LIKE CONCAT('%', #{search}, '%')
order by FAQ_NUM desc limit #{startRow}, #{pageSize}

mapper.update:
update FAQ
set FAQ_SUBJECT = #{subject}, FAQ_CONTENT = #{content}
where FAQ_NUM = #{num}
