mapper.select:
select QNA_NUM as "num", MEM_ID as "id", QNA_SUBJECT as "subject", QNA_QUESTION as "question", QNA_ANSWER as "answer", QNA_UPDATE as "update" from QNA
where QNA_SUBJECT like CONCAT('%', #{search}, '%') or QNA_QUESTION like CONCAT('%', #{search}, '%') or QNA_ANSWER like CONCAT('%', #{search}, '%')
order by QNA_NUM desc limit #{startRow}, #{pageSize}
