mapper.select:
select NOTICE_NUM as "num", ADMIN_ID as "id", NOTICE_TYPE as "type", NOTICE_SUBJECT as "subject", NOTICE_CONTENT as "content", NOTICE_DATE as "date", NOTICE_READCOUNT as "readcount", NOTICE_IMAGE as "image" from NOTICE
where NOTICE_TYPE = #{type}
order by NOTICE_NUM desc
limit #{startRow}, #{pageSize}

mapper.update:
update NOTICE
set NOTICE_READCOUNT = NOTICE_READCOUNT + 1
where NOTICE_NUM = #{num}

mapper.select:
select FAQ_NUM as "num", FAQ_SUBJECT as "subject" from FAQ
order by FAQ_NUM desc
limit #{startRow}, #{pageSize}

mapper.update:
update QNA
set QNA_ANSWER= #{content}
where QNA_NUM = #{num}

mapper.select:
select QNA_QUESTION as "content", QNA_SUBJECT as "subject", QNA_NUM as "num"
from QNA
WHERE QNA_NUM = #{num}

select QNA_SUBJECT as "subject", QNA_QUESTION as "question", QNA_NUM as "num", QNA_UPDATE as "update"
from QNA
WHERE MEM_ID = #{id}
order by QNA_NUM desc
limit #{startRow}, #{pageSize}

select count(*)
from QNA
WHERE MEM_ID = #{id}

select QNA_QUESTION as "question", QNA_ANSWER as "answer", QNA_SUBJECT as "subject", QNA_NUM as "num"
from QNA
WHERE QNA_NUM = #{num}

select LQNA_SUBJECT as "subject", LQNA_QUESTION as "question", LQNA_NUM as "num", LQNA_UPDATE as "update"
from LESSON_QNA
WHERE MEM_ID = #{id}
order by LQNA_NUM desc
limit #{startRow}, #{pageSize}

select count(*)
from QNA
WHERE MEM_ID = #{id}

select LQNA_QUESTION as "question", LQNA_ANSWER as "answer", LQNA_SUBJECT as "subject", LQNA_NUM as "num"
from LESSON_QNA
WHERE LQNA_NUM = #{num}

mapper.update:
update LESSON_QNA
set LQNA_ANSWER= #{answer}
where LQNA_NUM = #{num}

mapper.select:
select REVIEW_NUM as "num", REVIEW_CONTENT as "content", REVIEW_UPDATE as "update", REVIEW_RATING as "rating"
from REVIEW
WHERE MEM_ID = #{id}
order by REVIEW_NUM desc
limit #{startRow}, #{pageSize}

select count(*)
from REVIEW
WHERE MEM_ID = #{id}
