mapper.select:
select LES_SUBJECT as "subject", LES_UPDATE as "update", LES_NUM as "num", LES_STATUS as "status", LES_PREVIEW as "preview", LES_PRICE as "price"
from LESSON
where LES_CATEGORY = #{search}
and   LES_STATUS = 0
order by LES_NUM desc
limit #{startRow}, #{pageSize}

select LES_NUM as "num", LES_SUBJECT as "subject", LES_CONTENT as "content", LES_CATEGORY as "category", LES_SUBCATEGORY as "subCategory", LES_UPDATE as "update", LES_DATE as "date", LES_LOCATION as "location", LES_PRICE as "price", LES_PREVIEW as "preview", MEM_ID as "id" 
from LESSON
where LES_NUM = #{num}

select count(*)
from ORDERS
where MEM_ID = #{id}

select count(*)
from WISH
where MEM_ID = #{id}
